mapper.select:
select book.* from book
join recent_view on bo_code = rc_bo_code
where rc_ur_num = #{ur_num}

SELECT BO_CODE, BO_TITLE, BO_TOTAL_RATING, BO_REVIEW_COUNT, AU_NAME FROM book JOIN author ON book.BO_AU_NUM = author.AU_NUM WHERE BO_PUBLISH_DATE >= DATE_SUB(NOW(), INTERVAL 6 MONTH)
AND BO_ADULT = #{adultYN}
AND BO_FIN = #{finYN}
ORDER BY BO_TOTAL_RATING DESC, BO_REVIEW_COUNT DESC
ORDER BY BO_PUBLISH_DATE DESC
