mapper.select:
select book.* from book
join recent_view on bo_code = rc_bo_code
where rc_ur_num = #{ur_num}

SELECT BO_CODE, BO_TITLE, BO_TOTAL_RATING, BO_REVIEW_COUNT, AU_NAME AS bo_author, BO_TOTAL_EPISODE, BO_ADULT, BO_FIN, BO_PUBLISH_DATE FROM book JOIN author ON book.BO_AU_NUM = author.AU_NUM WHERE BO_PUBLISH_DATE >= DATE_SUB(NOW(), INTERVAL 6 MONTH)
AND BO_ADULT = #{adultYN}
ORDER BY BO_TOTAL_RATING DESC, BO_REVIEW_COUNT DESC
ORDER BY BO_PUBLISH_DATE DESC
LIMIT #{limit} OFFSET #{offset}

SELECT BO_CODE, BO_TITLE, BO_TOTAL_RATING, COUNT(DISTINCT RV_NUM) AS BO_REVIEW_COUNT, BO_FIN, BO_ADULT, BO_PUBLISH_DATE, BO_TOTAL_EPISODE, AU_NAME AS bo_author, IFNULL(r_recent.recent_review_count, 0) AS recent_review_count FROM book JOIN author ON BO_AU_NUM = AU_NUM LEFT JOIN review ON RV_BO_CODE = BO_CODE
LEFT JOIN( SELECT RV_BO_CODE, COUNT(*) AS recent_review_count FROM review
WHERE DATE(RV_DATE) = CURDATE()
WHERE RV_DATE >= DATE_SUB(CURDATE(), INTERVAL 7 DAY)
WHERE RV_DATE >= DATE_SUB(CURDATE(), INTERVAL 1 MONTH)
WHERE 1=1
GROUP BY RV_BO_CODE )r_recent ON r_recent.RV_BO_CODE = BO_CODE
AND BO_ADULT = #{adultYN}
AND BO_FIN = #{finished}
GROUP BY BO_CODE ORDER BY recent_review_count DESC, BO_TOTAL_RATING DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM (
    SELECT BO_CODE
    FROM book

        JOIN (
            SELECT BK_BO_CODE
            FROM book_keyword
            WHERE BK_KW_CODE IN

                #{id}

            GROUP BY BK_BO_CODE
            HAVING COUNT(DISTINCT BK_KW_CODE) = #{keywordCount}
        ) filtered ON BO_CODE = filtered.BK_BO_CODE

) AS result

SELECT LPAD(COUNT(*)+1,3,0) FROM BOOK
JOIN publisher_id ON BO_PI_NUM = PI_NUM
WHERE CONCAT(PI_PU_CODE, BO_SC_CODE)=#{psCode}

mapper.update:
update review_like
set rl_state = #{ur_state}
where rl_rv_num = #{rv_num} and rl_ur_num = #{ur_num}

update review
set rv_rating = null, rv_content = "삭제된 댓글입니다.", rv_del = "Y"
where rv_num = #{rv_num}

mapper.select:
select * from buy_list
join episode on bl_ep_code = ep_code
where bl_ur_num = #{ur_num} and ep_bo_code = #{bo_code}

select *, kw_name AS bk_name from book_keyword
join keyword on bk_kw_code = kw_code
where bk_bo_code = #{bo_code}

select * from notice
join publisher_id on nt_pi_num = pi_num
join user on pi_ur_num = ur_num
WHERE nt_bo_code = #{bo_code}
order by nt_date desc
LIMIT #{limit} OFFSET #{offset}
